function smallMenuButton(){var s=document.getElementById("small-menu");s.classList.contains("open")?s.classList.remove("open"):s.classList.add("open")}function modalControl(s){let e=document.getElementById(s),o=e.getElementsByClassName("modal-body")[0],t=e.getElementsByClassName("modal-content")[0];e.classList.contains("open")?(t.classList.remove("open"),document.body.classList.remove("no-overflow"),setTimeout(()=>{e.classList.remove("open"),o.classList.remove("no-overflow")},150)):(document.body.classList.add("no-overflow"),e.classList.add("open"),setTimeout(()=>{o.classList.add("show"),t.classList.add("open")},100))}